input[type=checkbox] {
    visibility: hidden;
}
width: 120px;
height: 40px;
background: #333;
margin: 10px 0px;

border-radius: 50px;
position: relative;

:before {
  content: '';
  position: absolute;
  top: 19px;
  left: 14px;
  height: 2px;
  width: 90px;
  background: #111;
}

label {
  display: block;
  width: 22px;
  height: 22px;
  border-radius: 50%;

  transition: all .5s ease;
  cursor: pointer;
  position: absolute;
  top: 7px;
  z-index: 1;
  left: 12px;
  background: #ddd;
}

input[type=checkbox]:checked + label {
    left: 84px;
    background: #26ca28;
}
